        public void TestImageEndpointBuilding()
        {
            HttpClient client = new();
            client.BaseAddress = new Uri("https://www.mapquestapi.com");
            string sessionId = "AJAA5wcAALIAAAAKAAAADwAAAKMAAAB42mNYzMDAyMTAwMCekVqUapWce0xoESuQy1B40nAfx8rji7sTWN2T1gLpHCDNgAXANAqFx_OA-HubNi5i2Ngp3b3qlXvSFCC98RV2jSDwzGhj1Ecg3ZDsxMUg4DDDoZlVgJFhggATC0jWwVGIwTGEk4kBABJ0JJA5I4A-:car";

            string endpoint1 = MapHandler.BuildRouteImageEndpoint(sessionId, "key", 100, 100, client);
            string endpoint2 = "https://www.mapquestapi.com/staticmap/v5/map?key=key&session=" + sessionId + "&size=100,100";

            Assert.That(endpoint1, Is.EqualTo(endpoint2));
